import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { postsAPI } from '../services/postsAPI';
import MediaGallery from './MediaGallery';
import { 
  Heart, 
  MessageCircle, 
  Eye, 
  MoreVertical,
  Calendar,
  HelpCircle,
  AlertCircle,
  Users,
  MessageCircle as DiscussionIcon,
  Video,
  Image,
  Play,
  Globe,
  Users as UsersIcon,
  Lock,
  EyeOff
} from 'lucide-react';
import toast from 'react-hot-toast';

const PostCard = ({ post, onUpdate }) => {
  const { user } = useAuth();
  const [isLiking, setIsLiking] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [isAddingComment, setIsAddingComment] = useState(false);
  const [replyingTo, setReplyingTo] = useState(null);
  const [replyContent, setReplyContent] = useState('');
  const [isAddingReply, setIsAddingReply] = useState(false);

  const postTypeIcons = {
    info: AlertCircle,
    event: Calendar,
    help: HelpCircle,
    announcement: Users,
    discussion: DiscussionIcon,
    live: Video
  };

  const postTypeColors = {
    info: 'bg-blue-100 text-blue-800',
    event: 'bg-green-100 text-green-800',
    help: 'bg-red-100 text-red-800',
    announcement: 'bg-purple-100 text-purple-800',
    discussion: 'bg-orange-100 text-orange-800',
    live: 'bg-red-100 text-red-800'
  };

  const postTypeLabels = {
    info: 'Information',
    event: 'Événement',
    help: 'Demande d\'aide',
    announcement: 'Annonce',
    discussion: 'Discussion',
    live: 'Live'
  };

  const privacyIcons = {
    public: Globe,
    friends: UsersIcon,
    private: Lock
  };

  const privacyLabels = {
    public: 'Public',
    friends: 'Amis',
    private: 'Privé'
  };

  const handleLike = async () => {
    if (isLiking) return;
    
    setIsLiking(true);
    try {
      if (post.is_liked_by_user) {
        await postsAPI.unlikePost(post.id);
        toast.success('Like retiré');
      } else {
        await postsAPI.likePost(post.id);
        toast.success('Post liké !');
      }
      
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Erreur lors du like:', error);
      toast.error('Erreur lors du like');
    } finally {
      setIsLiking(false);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    setIsAddingComment(true);
    try {
      await postsAPI.addComment(post.id, { content: newComment });
      setNewComment('');
      toast.success('Commentaire ajouté !');
      
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout du commentaire:', error);
      toast.error('Erreur lors de l\'ajout du commentaire');
    } finally {
      setIsAddingComment(false);
    }
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'À l\'instant';
    if (diffInMinutes < 60) return `Il y a ${diffInMinutes} min`;
    
    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `Il y a ${diffInHours}h`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `Il y a ${diffInDays}j`;
    
    return date.toLocaleDateString('fr-FR');
  };

  const PostTypeIcon = postTypeIcons[post.post_type] || AlertCircle;
  const PrivacyIcon = privacyIcons[post.privacy] || Globe;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
      {/* Header */}
      <div className="p-4">
        <div className="flex items-start space-x-3">
          <div className="flex-shrink-0">
            <img
              src={post.author.profile_picture || '/default-avatar.png'}
              alt={post.author.first_name}
              className="w-10 h-10 rounded-full object-cover"
            />
          </div>
          
          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2">
              <h4 className="text-sm font-semibold text-gray-900">
                {post.is_anonymous ? 'Anonyme' : `${post.author.first_name} ${post.author.last_name}`}
              </h4>
              
              {/* Badge Live */}
              {post.is_live_post && (
                <div className="flex items-center space-x-1 bg-red-100 text-red-800 px-2 py-1 rounded-full text-xs font-medium">
                  <div className="w-2 h-2 bg-red-600 rounded-full animate-pulse"></div>
                  <span>EN DIRECT</span>
                </div>
              )}
            </div>
            
            <div className="flex items-center space-x-2 text-xs text-gray-500 mt-1">
              <span>{formatTimestamp(post.created_at)}</span>
              <span>•</span>
              <span>{post.quartier.nom}, {post.quartier.commune.nom}</span>
              <span>•</span>
              <div className="flex items-center space-x-1">
                <PrivacyIcon className="w-3 h-3" />
                <span>{privacyLabels[post.privacy]}</span>
              </div>
            </div>
          </div>
          
          <div className="flex items-center space-x-2">
            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${postTypeColors[post.post_type]}`}>
              <PostTypeIcon className="w-3 h-3 mr-1" />
              {postTypeLabels[post.post_type]}
            </span>
            
            <button className="text-gray-400 hover:text-gray-600 transition-colors">
              <MoreVertical className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>

      {/* Contenu */}
      {post.content && (
        <div className="px-4 pb-4">
          <p className="text-gray-900 whitespace-pre-wrap">{post.content}</p>
        </div>
      )}

      {/* Médias */}
      {post.has_media && (
        <div className="px-4 pb-4">
          <MediaGallery 
            media={post.media_files} 
            className="rounded-lg overflow-hidden"
          />
        </div>
      )}

      {/* Live Stream */}
      {post.live_stream && (
        <div className="px-4 pb-4">
          <MediaGallery 
            media={[post.live_stream]} 
            className="rounded-lg overflow-hidden"
          />
        </div>
      )}

      {/* Statistiques */}
      <div className="px-4 py-3 border-t border-gray-100">
        <div className="flex items-center justify-between text-sm text-gray-500">
          <div className="flex items-center space-x-4">
            {post.likes_count > 0 && (
              <span>{post.likes_count} j'aime</span>
            )}
            {post.comments_count > 0 && (
              <span>{post.comments_count} commentaires</span>
            )}
            {post.views_count > 0 && (
              <span>{post.views_count} vues</span>
            )}
          </div>
          
          <div className="flex items-center space-x-1">
            <Eye className="w-4 h-4" />
            <span>{post.views_count}</span>
          </div>
        </div>
      </div>

      {/* Actions */}
      <div className="px-4 py-2 border-t border-gray-100">
        <div className="flex items-center justify-between">
          <button
            onClick={handleLike}
            disabled={isLiking}
            className={`flex items-center space-x-2 px-3 py-2 rounded-lg transition-colors ${
              post.is_liked_by_user
                ? 'text-red-600 bg-red-50 hover:bg-red-100'
                : 'text-gray-600 hover:bg-gray-100'
            }`}
          >
            <Heart className={`w-4 h-4 ${post.is_liked_by_user ? 'fill-current' : ''}`} />
            <span className="text-sm font-medium">J'aime</span>
          </button>
          
          <button
            onClick={() => setShowComments(!showComments)}
            className="flex items-center space-x-2 px-3 py-2 rounded-lg text-gray-600 hover:bg-gray-100 transition-colors"
          >
            <MessageCircle className="w-4 h-4" />
            <span className="text-sm font-medium">Commenter</span>
          </button>
        </div>
      </div>

      {/* Commentaires */}
      {showComments && (
        <div className="border-t border-gray-100">
          {/* Liste des commentaires */}
          <div className="px-4 py-3 max-h-64 overflow-y-auto">
            {post.comments && post.comments.length > 0 ? (
              <div className="space-y-3">
                {post.comments.map((comment) => (
                  <div key={comment.id} className="flex space-x-3">
                    <img
                      src={comment.author.profile_picture || '/default-avatar.png'}
                      alt={comment.author.first_name}
                      className="w-6 h-6 rounded-full object-cover flex-shrink-0"
                    />
                    <div className="flex-1">
                      <div className="bg-gray-50 rounded-lg px-3 py-2">
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-gray-900">
                            {comment.is_anonymous ? 'Anonyme' : `${comment.author.first_name} ${comment.author.last_name}`}
                          </span>
                          <span className="text-xs text-gray-500">
                            {formatTimestamp(comment.created_at)}
                          </span>
                        </div>
                        <p className="text-sm text-gray-700 mt-1">{comment.content}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-sm text-gray-500 text-center py-4">
                Aucun commentaire pour le moment
              </p>
            )}
          </div>

          {/* Formulaire de commentaire */}
          <div className="px-4 py-3 border-t border-gray-100">
            <form onSubmit={handleAddComment} className="flex space-x-3">
              <img
                src={user?.profile_picture || '/default-avatar.png'}
                alt={user?.first_name}
                className="w-8 h-8 rounded-full object-cover flex-shrink-0"
              />
              <div className="flex-1">
                <input
                  type="text"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Ajouter un commentaire..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
                  disabled={isAddingComment}
                />
              </div>
              <button
                type="submit"
                disabled={!newComment.trim() || isAddingComment}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors text-sm"
              >
                {isAddingComment ? 'Envoi...' : 'Envoyer'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default PostCard;
