import React, { useState, useEffect } from 'react';
import { 
  Play, 
  Pause, 
  Volume2, 
  VolumeX, 
  X, 
  ChevronLeft, 
  ChevronRight,
  Download,
  Fullscreen,
  Maximize2
} from 'lucide-react';
import { formatTime } from '../utils/timeUtils';
import LiveTimer from './LiveTimer';

const MediaGallery = ({ media, className = '' }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [showFullscreen, setShowFullscreen] = useState(false);


  if (!media || media.length === 0) return null;

  const currentMedia = media[currentIndex];
  const isVideo = currentMedia.media_type === 'video';
  const isLive = currentMedia.is_live;

  const nextMedia = () => {
    setCurrentIndex((prev) => (prev + 1) % media.length);
  };

  const prevMedia = () => {
    setCurrentIndex((prev) => (prev - 1 + media.length) % media.length);
  };





  const togglePlayPause = () => {
    if (isVideo) {
      const video = document.getElementById(`video-${currentMedia.id}`);
      if (video) {
        if (isPlaying) {
          video.pause();
        } else {
          video.play();
        }
        setIsPlaying(!isPlaying);
      }
    }
  };

  const toggleMute = () => {
    if (isVideo) {
      const video = document.getElementById(`video-${currentMedia.id}`);
      if (video) {
        video.muted = !isMuted;
        setIsMuted(!isMuted);
      }
    }
  };

  const handleVideoPlay = () => setIsPlaying(true);
  const handleVideoPause = () => setIsPlaying(false);

  const renderMediaContent = () => {
    if (isLive) {
      return (
        <div className="relative bg-black rounded-lg overflow-hidden">
          <video
            id={`video-${currentMedia.id}`}
            className="w-full h-full object-cover"
            autoPlay
            muted
            onPlay={handleVideoPlay}
            onPause={handleVideoPause}
          >
            <source src={currentMedia.file_url} type="video/mp4" />
          </video>
          
          {/* Badge Live avec chronom√®tre */}
          <div className="absolute top-4 left-4 bg-red-600 text-white px-3 py-1 rounded-full text-sm font-medium flex items-center space-x-1">
            <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
            <span>EN DIRECT</span>
            <LiveTimer 
              startTime={currentMedia.live_started_at}
              isActive={isLive}
              variant="compact"
              className="ml-2"
            />
          </div>
          
          {/* Spectateurs */}
          <div className="absolute top-4 right-4 bg-black bg-opacity-50 text-white px-3 py-1 rounded-full text-sm">
            {currentMedia.live_viewers_count} spectateurs
          </div>
        </div>
      );
    }

    if (isVideo) {
      return (
        <div className="relative bg-black rounded-lg overflow-hidden">
          <video
            id={`video-${currentMedia.id}`}
            className="w-full h-full object-cover"
            controls
            onPlay={handleVideoPlay}
            onPause={handleVideoPause}
          >
            <source src={currentMedia.file_url} type="video/mp4" />
          </video>
          
          {/* Contr√¥les personnalis√©s */}
          <div className="absolute bottom-4 left-4 right-4 flex items-center justify-between bg-black bg-opacity-50 rounded-lg p-2">
            <button
              onClick={togglePlayPause}
              className="text-white hover:text-gray-300 transition-colors"
            >
              {isPlaying ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5" />}
            </button>
            
            <button
              onClick={toggleMute}
              className="text-white hover:text-gray-300 transition-colors"
            >
              {isMuted ? <VolumeX className="w-5 h-5" /> : <Volume2 className="w-5 h-5" />}
            </button>
            
            <button
              onClick={() => setShowFullscreen(true)}
              className="text-white hover:text-gray-300 transition-colors"
            >
              <Maximize2 className="w-5 h-5" />
            </button>
          </div>
        </div>
      );
    }

    // Image
    return (
      <div className="relative">
        <img
          src={currentMedia.file_url}
          alt={currentMedia.title || 'Image'}
          className="w-full h-full object-cover rounded-lg"
        />
        
        {/* Bouton plein √©cran */}
        <button
          onClick={() => setShowFullscreen(true)}
          className="absolute top-4 right-4 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70 transition-colors"
        >
          <Maximize2 className="w-5 h-5" />
        </button>
      </div>
    );
  };

  const renderThumbnails = () => {
    if (media.length <= 1) return null;

    return (
      <div className="flex space-x-2 mt-3 overflow-x-auto">
        {media.map((item, index) => (
          <button
            key={item.id}
            onClick={() => setCurrentIndex(index)}
            className={`flex-shrink-0 w-16 h-16 rounded-lg overflow-hidden border-2 transition-all ${
              index === currentIndex
                ? 'border-blue-500'
                : 'border-gray-300 hover:border-gray-400'
            }`}
          >
            {item.media_type === 'video' ? (
              <div className="relative w-full h-full">
                <img
                  src={item.thumbnail_url || item.file_url}
                  alt={item.title || 'Vid√©o'}
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center">
                  <Play className="w-4 h-4 text-white" />
                </div>
              </div>
            ) : (
              <img
                src={item.file_url}
                alt={item.title || 'Image'}
                className="w-full h-full object-cover"
              />
            )}
          </button>
        ))}
      </div>
    );
  };

  const renderFullscreenModal = () => {
    if (!showFullscreen) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50">
        <div className="relative w-full h-full flex items-center justify-center">
          {/* Fermer */}
          <button
            onClick={() => setShowFullscreen(false)}
            className="absolute top-4 right-4 text-white hover:text-gray-300 transition-colors z-10"
          >
            <X className="w-8 h-8" />
          </button>

          {/* Navigation */}
          {media.length > 1 && (
            <>
              <button
                onClick={prevMedia}
                className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300 transition-colors z-10"
              >
                <ChevronLeft className="w-8 h-8" />
              </button>
              <button
                onClick={nextMedia}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300 transition-colors z-10"
              >
                <ChevronRight className="w-8 h-8" />
              </button>
            </>
          )}

          {/* Contenu */}
          <div className="max-w-4xl max-h-full p-4">
            {isVideo ? (
              <video
                src={currentMedia.file_url}
                className="max-w-full max-h-full object-contain"
                controls
                autoPlay
              />
            ) : (
              <img
                src={currentMedia.file_url}
                alt={currentMedia.title || 'Image'}
                className="max-w-full max-h-full object-contain"
              />
            )}
          </div>

          {/* Indicateurs */}
          {media.length > 1 && (
            <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
              {media.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentIndex(index)}
                  className={`w-3 h-3 rounded-full transition-colors ${
                    index === currentIndex ? 'bg-white' : 'bg-white bg-opacity-50'
                  }`}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className={className}>
      <div className="relative">
        {renderMediaContent()}
        
        {/* Navigation pour plusieurs m√©dias */}
        {media.length > 1 && (
          <>
            <button
              onClick={prevMedia}
              className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70 transition-colors"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>
            <button
              onClick={nextMedia}
              className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70 transition-colors"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </>
        )}
      </div>

      {renderThumbnails()}
      {renderFullscreenModal()}
    </div>
  );
};

export default MediaGallery;
