import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { postsAPI } from '../services/postsAPI';
import { 
  Heart, 
  MessageCircle, 
  Eye, 
  MoreVertical,
  Calendar,
  HelpCircle,
  AlertCircle,
  Users,
  MessageCircle as DiscussionIcon
} from 'lucide-react';
import toast from 'react-hot-toast';

const PostCard = ({ post, onUpdate }) => {
  const { user } = useAuth();
  const [isLiking, setIsLiking] = useState(false);
  const [showComments, setShowComments] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [isAddingComment, setIsAddingComment] = useState(false);

  const postTypeIcons = {
    info: AlertCircle,
    event: Calendar,
    help: HelpCircle,
    announcement: Users,
    discussion: DiscussionIcon
  };

  const postTypeColors = {
    info: 'bg-blue-100 text-blue-800',
    event: 'bg-green-100 text-green-800',
    help: 'bg-red-100 text-red-800',
    announcement: 'bg-purple-100 text-purple-800',
    discussion: 'bg-orange-100 text-orange-800'
  };

  const postTypeLabels = {
    info: 'Information',
    event: 'Événement',
    help: 'Demande d\'aide',
    announcement: 'Annonce',
    discussion: 'Discussion'
  };

  const handleLike = async () => {
    if (isLiking) return;
    
    setIsLiking(true);
    try {
      if (post.is_liked_by_user) {
        await postsAPI.unlikePost(post.id);
        toast.success('Like retiré');
      } else {
        await postsAPI.likePost(post.id);
        toast.success('Post liké !');
      }
      
      // Mettre à jour l'état local
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Erreur lors du like:', error);
      toast.error('Erreur lors du like');
    } finally {
      setIsLiking(false);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    
    if (!newComment.trim()) return;
    
    setIsAddingComment(true);
    try {
      await postsAPI.addComment(post.id, { content: newComment });
      setNewComment('');
      toast.success('Commentaire ajouté !');
      
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout du commentaire:', error);
      toast.error('Erreur lors de l\'ajout du commentaire');
    } finally {
      setIsAddingComment(false);
    }
  };

  const handlePostClick = async () => {
    try {
      await postsAPI.incrementViews(post.id);
      // Optionnel : mettre à jour l'état local
      if (onUpdate) {
        onUpdate();
      }
    } catch (error) {
      console.error('Erreur lors de l\'incrémentation des vues:', error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) {
      return 'À l\'instant';
    } else if (diffInHours < 24) {
      return `Il y a ${diffInHours}h`;
    } else {
      return date.toLocaleDateString('fr-FR', {
        day: 'numeric',
        month: 'short',
        hour: '2-digit',
        minute: '2-digit'
      });
    }
  };

  const Icon = postTypeIcons[post.post_type] || AlertCircle;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-4">
      {/* Header */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
            <span className="text-green-600 font-semibold">
              {post.author?.first_name?.[0] || post.author?.username?.[0] || 'U'}
            </span>
          </div>
          <div>
            <div className="flex items-center space-x-2">
              <span className="font-medium text-gray-900">
                {post.is_anonymous ? 'Anonyme' : (post.author?.first_name || post.author?.username)}
              </span>
              <span className={`px-2 py-1 text-xs rounded-full ${postTypeColors[post.post_type]}`}>
                <Icon className="w-3 h-3 inline mr-1" />
                {postTypeLabels[post.post_type]}
              </span>
            </div>
            <div className="text-sm text-gray-500">
              {post.quartier?.nom}, {post.quartier?.commune?.nom} • {formatDate(post.created_at)}
            </div>
          </div>
        </div>
        
        {post.author?.id === user?.id && (
          <button className="text-gray-400 hover:text-gray-600">
            <MoreVertical className="w-5 h-5" />
          </button>
        )}
      </div>

      {/* Content */}
      <div className="mb-4">
        {post.title && (
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            {post.title}
          </h3>
        )}
        <p className="text-gray-700 whitespace-pre-wrap">
          {post.content}
        </p>
      </div>

      {/* Stats */}
      <div className="flex items-center justify-between text-sm text-gray-500 mb-4">
        <div className="flex items-center space-x-4">
          <span className="flex items-center space-x-1">
            <Eye className="w-4 h-4" />
            <span>{post.views_count} vues</span>
          </span>
          <span className="flex items-center space-x-1">
            <MessageCircle className="w-4 h-4" />
            <span>{post.comments_count} commentaires</span>
          </span>
        </div>
      </div>

      {/* Actions */}
      <div className="flex items-center justify-between pt-4 border-t border-gray-100">
        <div className="flex items-center space-x-4">
          <button
            onClick={handleLike}
            disabled={isLiking}
            className={`flex items-center space-x-2 px-3 py-2 rounded-md transition-colors ${
              post.is_liked_by_user
                ? 'text-red-600 bg-red-50'
                : 'text-gray-500 hover:text-red-600 hover:bg-red-50'
            }`}
          >
            <Heart className={`w-5 h-5 ${post.is_liked_by_user ? 'fill-current' : ''}`} />
            <span>{post.likes_count}</span>
          </button>
          
          <button
            onClick={() => setShowComments(!showComments)}
            className="flex items-center space-x-2 px-3 py-2 rounded-md text-gray-500 hover:text-blue-600 hover:bg-blue-50 transition-colors"
          >
            <MessageCircle className="w-5 h-5" />
            <span>Commenter</span>
          </button>
        </div>
      </div>

      {/* Comments Section */}
      {showComments && (
        <div className="mt-4 pt-4 border-t border-gray-100">
          {/* Add Comment */}
          <form onSubmit={handleAddComment} className="mb-4">
            <div className="flex space-x-2">
              <input
                type="text"
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Ajouter un commentaire..."
                className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500"
              />
              <button
                type="submit"
                disabled={isAddingComment || !newComment.trim()}
                className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {isAddingComment ? '...' : 'Envoyer'}
              </button>
            </div>
          </form>

          {/* Comments List */}
          <div className="space-y-3">
            {post.comments?.map((comment) => (
              <div key={comment.id} className="flex space-x-3">
                <div className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center">
                  <span className="text-gray-600 text-sm font-medium">
                    {comment.author?.first_name?.[0] || comment.author?.username?.[0] || 'U'}
                  </span>
                </div>
                <div className="flex-1">
                  <div className="bg-gray-50 rounded-lg p-3">
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-sm font-medium text-gray-900">
                        {comment.is_anonymous ? 'Anonyme' : (comment.author?.first_name || comment.author?.username)}
                      </span>
                      <span className="text-xs text-gray-500">
                        {formatDate(comment.created_at)}
                      </span>
                    </div>
                    <p className="text-sm text-gray-700">
                      {comment.content}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PostCard;
