import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { postsAPI } from '../services/postsAPI';
import CreatePost from '../components/CreatePost';
import PostCard from '../components/PostCard';
import { 
  Users, 
  MapPin, 
  MessageCircle, 
  Bell, 
  Settings, 
  LogOut,
  Plus,
  Search,
  Filter
} from 'lucide-react';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [isLoading, setIsLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [showCreatePost, setShowCreatePost] = useState(false);
  const [postsLoading, setPostsLoading] = useState(false);

  // Mock data - à remplacer par des vraies données API
  const [stats] = useState({
    neighbors: 24,
    posts: 156,
    events: 8,
    messages: 42
  });

  const [recentPosts] = useState([
    {
      id: 1,
      author: 'Mamadou Diallo',
      content: 'Réunion du quartier ce samedi à 15h au centre communautaire',
      timestamp: '2h',
      type: 'event'
    },
    {
      id: 2,
      author: 'Fatou Camara',
      content: 'Quelqu\'un a vu mon chat ? Il s\'est échappé hier soir',
      timestamp: '4h',
      type: 'help'
    },
    {
      id: 3,
      author: 'Ibrahima Bah',
      content: 'Coupure d\'eau prévue demain de 8h à 12h pour maintenance',
      timestamp: '6h',
      type: 'info'
    }
  ]);

  const handleLogout = async () => {
    try {
      await logout();
      toast.success('Déconnexion réussie');
    } catch (error) {
      toast.error('Erreur lors de la déconnexion');
    }
  };

  const handleCreatePost = () => {
    setShowCreatePost(true);
  };

  const handlePostCreated = (newPost) => {
    setPosts(prevPosts => [newPost, ...prevPosts]);
  };

  const fetchPosts = async () => {
    setPostsLoading(true);
    try {
      const response = await postsAPI.getPosts();
      setPosts(response.results || response);
    } catch (error) {
      console.error('Erreur lors du chargement des posts:', error);
      toast.error('Erreur lors du chargement des messages');
    } finally {
      setPostsLoading(false);
    }
  };

  useEffect(() => {
    if (activeTab === 'posts') {
      fetchPosts();
    }
  }, [activeTab]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <h1 className="text-2xl font-bold text-gray-900">
                Tableau de bord
              </h1>
              <div className="flex items-center text-sm text-gray-500">
                <MapPin className="w-4 h-4 mr-1" />
                {user?.quartier?.nom}, {user?.quartier?.commune?.nom}
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <Bell className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600">
                <Settings className="w-5 h-5" />
              </button>
              <button
                onClick={handleLogout}
                className="flex items-center text-gray-600 hover:text-red-600 transition-colors duration-200"
              >
                <LogOut className="w-4 h-4 mr-1" />
                Déconnexion
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <div className="bg-gradient-to-r from-green-600 to-green-700 rounded-lg p-6 mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-white mb-2">
                Bonjour, {user?.first_name || user?.username} !
              </h2>
              <p className="text-green-100">
                Bienvenue dans votre communauté locale
              </p>
            </div>
            <button
              onClick={handleCreatePost}
              className="bg-white text-green-600 px-4 py-2 rounded-lg font-medium hover:bg-gray-50 transition-colors duration-200 flex items-center"
            >
              <Plus className="w-4 h-4 mr-2" />
              Nouveau message
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg p-6 shadow-sm">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-lg">
                <Users className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Voisins</p>
                <p className="text-2xl font-bold text-gray-900">{stats.neighbors}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg p-6 shadow-sm">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-lg">
                <MessageCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Messages</p>
                <p className="text-2xl font-bold text-gray-900">{stats.posts}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg p-6 shadow-sm">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-lg">
                <Bell className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Événements</p>
                <p className="text-2xl font-bold text-gray-900">{stats.events}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg p-6 shadow-sm">
            <div className="flex items-center">
              <div className="bg-orange-100 p-3 rounded-lg">
                <MessageCircle className="w-6 h-6 text-orange-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Conversations</p>
                <p className="text-2xl font-bold text-gray-900">{stats.messages}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-sm mb-8">
          <div className="border-b border-gray-200">
            <nav className="flex space-x-8 px-6">
              {[
                { id: 'overview', label: 'Vue d\'ensemble' },
                { id: 'posts', label: 'Messages' },
                { id: 'events', label: 'Événements' },
                { id: 'neighbors', label: 'Voisins' }
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === tab.id
                      ? 'border-green-500 text-green-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  {tab.label}
                </button>
              ))}
            </nav>
          </div>

          <div className="p-6">
            {activeTab === 'overview' && (
              <div className="space-y-6">
                {/* Recent Posts */}
                <div>
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900">
                      Messages récents
                    </h3>
                    <button 
                      onClick={() => setActiveTab('posts')}
                      className="text-green-600 hover:text-green-700 text-sm font-medium"
                    >
                      Voir tout
                    </button>
                  </div>
                  <div className="space-y-4">
                    {posts.slice(0, 3).map((post) => (
                      <PostCard key={post.id} post={post} onUpdate={fetchPosts} />
                    ))}
                    {posts.length === 0 && !postsLoading && (
                      <div className="text-center py-8 text-gray-500">
                        Aucun message pour le moment
                      </div>
                    )}
                  </div>
                </div>

                {/* Quick Actions */}
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">
                    Actions rapides
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <button className="bg-green-50 border border-green-200 rounded-lg p-4 text-left hover:bg-green-100 transition-colors duration-200">
                      <MessageCircle className="w-6 h-6 text-green-600 mb-2" />
                      <h4 className="font-medium text-gray-900">Nouveau message</h4>
                      <p className="text-sm text-gray-600">Partager une information</p>
                    </button>
                    <button className="bg-blue-50 border border-blue-200 rounded-lg p-4 text-left hover:bg-blue-100 transition-colors duration-200">
                      <Bell className="w-6 h-6 text-blue-600 mb-2" />
                      <h4 className="font-medium text-gray-900">Créer un événement</h4>
                      <p className="text-sm text-gray-600">Organiser une activité</p>
                    </button>
                    <button className="bg-purple-50 border border-purple-200 rounded-lg p-4 text-left hover:bg-purple-100 transition-colors duration-200">
                      <Users className="w-6 h-6 text-purple-600 mb-2" />
                      <h4 className="font-medium text-gray-900">Inviter un voisin</h4>
                      <p className="text-sm text-gray-600">Élargir la communauté</p>
                    </button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'posts' && (
              <div className="space-y-6">
                <div className="flex items-center justify-between mb-6">
                  <h3 className="text-lg font-semibold text-gray-900">
                    Messages de la communauté
                  </h3>
                  <button
                    onClick={() => setShowCreatePost(true)}
                    className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition-colors flex items-center space-x-2"
                  >
                    <Plus className="w-4 h-4" />
                    <span>Nouveau message</span>
                  </button>
                </div>
                
                {postsLoading ? (
                  <div className="text-center py-8">
                    <div className="spinner mx-auto mb-4"></div>
                    <p className="text-gray-500">Chargement des messages...</p>
                  </div>
                ) : posts.length > 0 ? (
                  <div className="space-y-4">
                    {posts.map((post) => (
                      <PostCard key={post.id} post={post} onUpdate={fetchPosts} />
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <MessageCircle className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">
                      Aucun message pour le moment
                    </h3>
                    <p className="text-gray-500 mb-6">
                      Soyez le premier à partager quelque chose avec votre communauté !
                    </p>
                    <button
                      onClick={() => setShowCreatePost(true)}
                      className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition-colors"
                    >
                      Créer le premier message
                    </button>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'events' && (
              <div className="text-center py-12">
                <Bell className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  Événements locaux
                </h3>
                <p className="text-gray-500">
                  Fonctionnalité en cours de développement
                </p>
              </div>
            )}

            {activeTab === 'neighbors' && (
              <div className="text-center py-12">
                <Users className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  Voisins du quartier
                </h3>
                <p className="text-gray-500">
                  Fonctionnalité en cours de développement
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
