import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { postsAPI } from '../services/postsAPI';
import { 
  MessageCircle, 
  Calendar, 
  HelpCircle, 
  AlertCircle, 
  Users,
  Send,
  X,
  Image,
  Trash2,
  Video as VideoIcon
} from 'lucide-react';
import toast from 'react-hot-toast';

const MAX_IMAGE_SIZE = 5 * 1024 * 1024; // 5MB
const MAX_VIDEO_SIZE = 50 * 1024 * 1024; // 50MB
const VIDEO_TYPES = [
  'video/mp4',
  'video/webm',
  'video/quicktime', // mov
];

const CreatePost = ({ isOpen, onClose, onPostCreated }) => {
  const { user } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [videoPreview, setVideoPreview] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    post_type: 'info',
    is_anonymous: false
  });

  const postTypes = [
    { value: 'info', label: 'Information', icon: AlertCircle, color: 'text-blue-600' },
    { value: 'event', label: 'Événement', icon: Calendar, color: 'text-green-600' },
    { value: 'help', label: 'Demande d\'aide', icon: HelpCircle, color: 'text-red-600' },
    { value: 'announcement', label: 'Annonce', icon: Users, color: 'text-purple-600' },
    { value: 'discussion', label: 'Discussion', icon: MessageCircle, color: 'text-orange-600' }
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        toast.error('Veuillez sélectionner une image valide');
        return;
      }
      if (file.size > MAX_IMAGE_SIZE) {
        toast.error('L\'image ne doit pas dépasser 5MB');
        return;
      }
      if (selectedVideo) {
        toast.error('Vous ne pouvez ajouter qu\'une image ou une vidéo, pas les deux.');
        return;
      }
      setSelectedImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!VIDEO_TYPES.includes(file.type)) {
        toast.error('Format vidéo non supporté (mp4, webm, mov)');
        return;
      }
      if (file.size > MAX_VIDEO_SIZE) {
        toast.error('La vidéo ne doit pas dépasser 50MB');
        return;
      }
      if (selectedImage) {
        toast.error('Vous ne pouvez ajouter qu\'une image ou une vidéo, pas les deux.');
        return;
      }
      setSelectedVideo(file);
      setVideoPreview(URL.createObjectURL(file));
    }
  };

  const removeImage = () => {
    setSelectedImage(null);
    setImagePreview(null);
    const fileInput = document.getElementById('image-input');
    if (fileInput) fileInput.value = '';
  };

  const removeVideo = () => {
    setSelectedVideo(null);
    setVideoPreview(null);
    const fileInput = document.getElementById('video-input');
    if (fileInput) fileInput.value = '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.content.trim()) {
      toast.error('Le contenu du message est requis');
      return;
    }
    setIsLoading(true);
    try {
      const submitData = new FormData();
      submitData.append('title', formData.title);
      submitData.append('content', formData.content);
      submitData.append('post_type', formData.post_type);
      submitData.append('is_anonymous', formData.is_anonymous);
      if (selectedImage) submitData.append('image', selectedImage);
      if (selectedVideo) submitData.append('video', selectedVideo);
      const response = await postsAPI.createPost(submitData);
      toast.success('Message publié avec succès !');
      setFormData({ title: '', content: '', post_type: 'info', is_anonymous: false });
      setSelectedImage(null); setImagePreview(null);
      setSelectedVideo(null); setVideoPreview(null);
      onPostCreated(response);
      onClose();
    } catch (error) {
      console.error('Erreur lors de la création du post:', error);
      toast.error('Erreur lors de la publication du message');
    } finally {
      setIsLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl mx-4 max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <h2 className="text-xl font-semibold text-gray-900">Nouveau message</h2>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600 transition-colors">
            <X className="w-6 h-6" />
          </button>
        </div>
        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Type de post */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">Type de message</label>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              {postTypes.map((type) => {
                const Icon = type.icon;
                return (
                  <button
                    key={type.value}
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, post_type: type.value }))}
                    className={`p-3 rounded-lg border-2 transition-all ${formData.post_type === type.value ? 'border-green-500 bg-green-50' : 'border-gray-200 hover:border-gray-300'}`}
                  >
                    <div className="flex items-center space-x-2">
                      <Icon className={`w-5 h-5 ${type.color}`} />
                      <span className="text-sm font-medium">{type.label}</span>
                    </div>
                  </button>
                );
              })}
            </div>
          </div>
          {/* Titre (optionnel) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Titre (optionnel)</label>
            <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="Titre de votre message..." className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" />
          </div>
          {/* Contenu */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Contenu <span className="text-red-500">*</span></label>
            <textarea name="content" value={formData.content} onChange={handleChange} placeholder="Partagez votre message avec votre communauté..." rows={6} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 resize-none" required />
          </div>
          {/* Upload d'image ou vidéo */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Image ou vidéo (optionnel)</label>
            {/* Image */}
            {!imagePreview && !videoPreview && (
              <div className="flex space-x-4">
                <div className="flex-1 border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
                  <input id="image-input" type="file" accept="image/*" onChange={handleImageChange} className="hidden" />
                  <label htmlFor="image-input" className="cursor-pointer flex flex-col items-center">
                    <Image className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                    <span className="text-sm text-gray-600">Image (PNG, JPG, GIF jusqu'à 5MB)</span>
                  </label>
                </div>
                <div className="flex-1 border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
                  <input id="video-input" type="file" accept="video/mp4,video/webm,video/quicktime" onChange={handleVideoChange} className="hidden" />
                  <label htmlFor="video-input" className="cursor-pointer flex flex-col items-center">
                    <VideoIcon className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                    <span className="text-sm text-gray-600">Vidéo (MP4, WebM, MOV jusqu'à 50MB)</span>
                  </label>
                </div>
              </div>
            )}
            {/* Aperçu image */}
            {imagePreview && (
              <div className="relative mt-2">
                <img src={imagePreview} alt="Aperçu" className="w-full h-48 object-cover rounded-lg" />
                <button type="button" onClick={removeImage} className="absolute top-2 right-2 bg-red-500 text-white p-1 rounded-full hover:bg-red-600 transition-colors">
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            )}
            {/* Aperçu vidéo */}
            {videoPreview && (
              <div className="relative mt-2">
                <video src={videoPreview} controls className="w-full h-48 rounded-lg bg-black" />
                <button type="button" onClick={removeVideo} className="absolute top-2 right-2 bg-red-500 text-white p-1 rounded-full hover:bg-red-600 transition-colors">
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            )}
          </div>
          {/* Options */}
          <div className="flex items-center space-x-4">
            <label className="flex items-center space-x-2">
              <input type="checkbox" name="is_anonymous" checked={formData.is_anonymous} onChange={handleChange} className="h-4 w-4 text-green-600 focus:ring-green-500 border-gray-300 rounded" />
              <span className="text-sm text-gray-700">Publier anonymement</span>
            </label>
          </div>
          {/* Actions */}
          <div className="flex items-center justify-end space-x-3 pt-4 border-t">
            <button type="button" onClick={onClose} className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 transition-colors">Annuler</button>
            <button type="submit" disabled={isLoading || !formData.content.trim()} className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2">
              {isLoading ? (<><div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div><span>Publication...</span></>) : (<><Send className="w-4 h-4" /><span>Publier</span></>)}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreatePost;
